function mustacheBuildOptions(object) {
  var validTypes = ['string', 'number', 'boolean'];
  var value;
  var key;
  for (key in object) {
    value = object[key];
    if (object.hasOwnProperty(key) && validTypes.indexOf(typeof value) !== -1) {
      object[key + '=' + value] = true;
    }
  }
  return object;
}

